import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';

const ThreeScene = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    console.log('ThreeScene: Initializing...');

    // Create Scene
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf5f5f5);

    // Get container dimensions
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;

    // Create Camera
    const camera = new THREE.PerspectiveCamera(
      75,           // Field of view
      width / height, // Aspect ratio
      0.1,          // Near clipping plane
      1000          // Far clipping plane
    );
    camera.position.set(0, 0, 5); // Position camera

    // Create Renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mountRef.current.appendChild(renderer.domElement);

    // Add Lighting
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);

    // Add hemisphere light for better illumination
    const hemisphereLight = new THREE.HemisphereLight(0xffffbb, 0x080820, 0.5);
    scene.add(hemisphereLight);

    // Load GLTF Model
    const loader = new GLTFLoader();
    let model;

    loader.load(
      '/models/scene.gltf',
      (gltf) => {
        model = gltf.scene;

        // Calculate bounding box and center the model
        const box = new THREE.Box3().setFromObject(model);
        const center = box.getCenter(new THREE.Vector3());
        const size = box.getSize(new THREE.Vector3());

        // Center the model
        model.position.sub(center);

        // Scale the model to fit in the camera view
        const maxDimension = Math.max(size.x, size.y, size.z);
        const scale = 2 / maxDimension; // Scale to fit within 2 units
        model.scale.setScalar(scale);

        scene.add(model);
        console.log('Model loaded successfully!', { size, center, scale });

        // Adjust camera position based on model size
        const distance = maxDimension * 2;
        camera.position.set(distance, distance, distance);
        camera.lookAt(0, 0, 0);
      },
      (progress) => {
        console.log('Loading progress:', (progress.loaded / progress.total) * 100 + '%');
      },
      (error) => {
        console.error('Error loading model:', error);
        // Add a fallback cube to test if the scene is working
        const geometry = new THREE.BoxGeometry(1, 1, 1);
        const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
        const cube = new THREE.Mesh(geometry, material);
        scene.add(cube);
        console.log('Added fallback cube due to model loading error');
      }
    );

    // Animation Loop
    const animate = () => {
      requestAnimationFrame(animate);

      // Rotate model slowly
      if (model) {
        model.rotation.y += 0.005;
      }

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      const newWidth = mountRef.current.clientWidth;
      const newHeight = mountRef.current.clientHeight;

      camera.aspect = newWidth / newHeight;
      camera.updateProjectionMatrix();

      renderer.setSize(newWidth, newHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
      mountRef.current?.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: '100%',
        height: '100%',
        borderRadius: '15px',
        overflow: 'hidden',
      }}
    />
  );
};

export default ThreeScene;
