// src/components/ThreeScene.js
import React, { useEffect, useRef } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

const ThreeScene = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    // === Basic Setup ===
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    // === Camera ===
    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
    camera.position.set(0, 0, 3);

    // === Renderer ===
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;
    renderer.setSize(width, height);
    renderer.shadowMap.enabled = true;
    mountRef.current.appendChild(renderer.domElement);

    // === Lights ===
    const ambientLight = new THREE.AmbientLight(0xffffff, 1.2);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1.2);
    directionalLight.position.set(2, 3, 4);
    scene.add(directionalLight);

    // === Controls ===
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // === Helper (to verify scene works) ===
    const axesHelper = new THREE.AxesHelper(2);
    scene.add(axesHelper);

    // === Load Ashoka Chakra Model ===
    const loader = new GLTFLoader();
    let chakraModel = null;

    console.log("📦 Loading Chakra model...");

    loader.load(
      "/models/scene.gltf",
      (gltf) => {
        chakraModel = gltf.scene;
        chakraModel.scale.set(0.5, 0.5, 0.5);
        chakraModel.position.set(0, -0.5, 0);
        chakraModel.rotation.x = Math.PI / 2;
        scene.add(chakraModel);
        console.log("✅ Model loaded and added to scene!");
      },
      undefined,
      (error) => {
        console.error("❌ Error loading GLTF:", error);
      }
    );

    // === Animation Loop (NO ROTATION) ===
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    // === Handle Resize ===
    const handleResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };
    window.addEventListener("resize", handleResize);

    // === Cleanup ===
    return () => {
      window.removeEventListener("resize", handleResize);
      mountRef.current.removeChild(renderer.domElement);
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: "100%",
        height: "100%",
        overflow: "hidden",
      }}
    />
  );
};

export default ThreeScene;
