// src/components/ThreeScene.js
import React, { useEffect, useRef } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

const ThreeScene = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    // === Scene Setup ===
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xfafafa);

    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
    camera.position.set(0, 0.5, 3);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    const width = mountRef.current.clientWidth;
    const height = mountRef.current.clientHeight;
    renderer.setSize(width, height);
    renderer.shadowMap.enabled = true;
    mountRef.current.appendChild(renderer.domElement);

    // === Lighting ===
    const ambientLight = new THREE.AmbientLight(0xffffff, 1.0);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1.2);
    directionalLight.position.set(3, 5, 5);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444, 0.5);
    hemiLight.position.set(0, 1, 0);
    scene.add(hemiLight);

    // === Reference Plane (optional but helps confirm visibility) ===
    const planeGeo = new THREE.PlaneGeometry(10, 10);
    const planeMat = new THREE.MeshStandardMaterial({ color: 0xffffff });
    const plane = new THREE.Mesh(planeGeo, planeMat);
    plane.rotation.x = -Math.PI / 2;
    plane.position.y = -1;
    scene.add(plane);

    // === Controls ===
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;

    // === Load Ashoka Chakra Model ===
    const loader = new GLTFLoader();
    let chakraModel = null;

    loader.load(
      "/models/scene.gltf",
      (gltf) => {
        chakraModel = gltf.scene;

        // Bounding box to center and scale model
        const box = new THREE.Box3().setFromObject(chakraModel);
        const size = new THREE.Vector3();
        box.getSize(size);
        const center = new THREE.Vector3();
        box.getCenter(center);

        // Normalize and scale up slightly
        const maxDim = Math.max(size.x, size.y, size.z);
        const scale = 2 / maxDim; // scale up 2×
        chakraModel.scale.set(scale, scale, scale);
        chakraModel.position.sub(center.multiplyScalar(scale));

        // Adjust rotation
        chakraModel.rotation.x = Math.PI / 2;

        scene.add(chakraModel);

        // Adjust camera distance based on model size
        const distance = 2.5 / scale;
        camera.position.set(0, 0.5, distance);
        camera.lookAt(0, 0, 0);

        console.log("✅ Model loaded and added to scene");
      },
      (xhr) => {
        console.log(
          `Model loading: ${
            isFinite(xhr.loaded / xhr.total)
              ? ((xhr.loaded / xhr.total) * 100).toFixed(1)
              : "..."
          }%`
        );
      },
      (error) => {
        console.error("❌ Error loading GLTF:", error);
      }
    );

    // === Animation Loop ===
    const animate = () => {
      requestAnimationFrame(animate);
      if (chakraModel) chakraModel.rotation.z += 0.01;
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    // === Handle Resize ===
    const handleResize = () => {
      const width = mountRef.current.clientWidth;
      const height = mountRef.current.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };
    window.addEventListener("resize", handleResize);

    // === Cleanup ===
    return () => {
      window.removeEventListener("resize", handleResize);
      mountRef.current?.removeChild(renderer.domElement);
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={mountRef}
      style={{
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    />
  );
};

export default ThreeScene;
